package otel

import (
	"bytes"
	"context"
	"fmt"
	"github.com/stretchr/testify/assert"
	"go.opentelemetry.io/otel/exporters/stdout/stdouttrace"
	tracesdk "go.opentelemetry.io/otel/sdk/trace"
	"testing"
)

const PROT_16_1 string = "[1, \"NR_LAMBDA_MONITORING\", \"\"]"
const PROT_16_2 string = "[2, \"NR_LAMBDA_MONITORING\", {\"arn\":\"arn:aws:lambda:us-east-1:463657938898:function:javimb-test-decorator\",\"protocol_version\":16,\"metadata_version\":2,\"execution_environment\":\"AWS_Lambda_python3.6\",\"agent_version\":\"4.2.0.100\",\"agent_language\":\"python\"}, \"\"]"
const GO_AGENT string = "[2,\"NR_LAMBDA_MONITORING\",\n  {\"metadata_version\":2,\"arn\":\"arn:aws:lambda:us-east-1:123456789012:function:my-function\",\"protocol_version\":17,\"function_version\":\"$LATEST\",\"agent_version\":\"2.2.0.lambda-beta\",\"agent_language\":\"go\"},\"\"]"

func TestOtelTelemetrySender_SendTelemetry(t *testing.T) {
	var buf bytes.Buffer
	exporter, err := stdouttrace.New(stdouttrace.WithPrettyPrint(), stdouttrace.WithWriter(&buf))
	assert.NoError(t, err)

	sender := OtelTelemetrySender{traceProvider: tracesdk.NewTracerProvider(tracesdk.WithSyncer(exporter))}
	ctx := context.Background()

	err, count := sender.SendTelemetry(ctx, "test", [][]byte{[]byte(PROT_16_1), []byte(PROT_16_2), []byte(GO_AGENT)})

	assert.NoError(t, err)
	assert.Equal(t, 3, count)

	fmt.Println(buf.String())
}
